                Color = "green";
                text = "Welcome Guest!";

                UriHelper.NavigateTo("/");

            }
            else
            {
                Color = "red";
                text = "Please fill in all the fields!";

            }

        }
        else
        {
            Color = "red";
            text = "Passwords dont match, retry!";

